import matplotlib as mplimport numpy as npimport matplotlib.pyplot as pltimport matplotlib.animation as manimationplt.rcParams['animation.ffmpeg_path'] = '/usr/local/bin/ffmpeg'data = np.loadtxt('arrayanim.txt')T = (int)(data[0])FFMpegWriter = manimation.writers['ffmpeg']writer = FFMpegWriter(fps=data[1])mpl.rcParams['legend.fontsize'] = data[2]fig = plt.figure()writer.setup(fig, "video.avi" , data[3])x = np.arange(data[4], data[5], data[6])for t in range(0, T):    print('Drawing frame %d...' % (t))    ax = plt.subplot()    y1 = 6*x*x    y2 = 6*x*x*np.sin(x*t)    y3 = -6*x*x    plt.ylim(data[7], data[8])    plt.plot(x,y1)    plt.plot(x,y2)    plt.plot(x,y3)    ax.fill_between(x, y1, y2, where=y1>y2, interpolate=True,                    color='yellow', alpha=0.3)    ax.fill_between(x, y3, y2, where=y2>y3, interpolate=True,                    color='blue', alpha=0.3)    plt.title("My first animation")    writer.grab_frame()    plt.clf()